/* ==UserStyle==
@name           Discord Catppuccin
@namespace      github.com/catppuccin/discord
@version        0.2.1
@description    Soothing pastel theme for Discord
@author         Catppuccin
@preprocessor   stylus
@var select theme "Theme" ["latte:Latte", "frappe:Frappe", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accent "Accent" {"Rosewater": "'rosewater'", "Flamingo": "'flamingo'", "Pink": "'pink'", "Mauve": "'mauve'", "Red": "'red'", "Maroon": "'maroon'", "Peach": "'peach'", "Yellow": "'yellow'", "Green": "'green'", "Teal": "'teal'", "Blue*": "'blue'", "Sapphire": "'sapphire'", "Sky": "'sky'", "Lavender": "'lavender'"}
==/UserStyle== */

@-moz-document regexp("https?://(canary\\.|ptb\\.|)discord.com/.*") {
    @import url("https://catppuccin.github.io/discord/dist/catppuccin-" + theme + "-" + accent + ".theme.css")

    *{
        font-family: FiraCode Nerd Font Mono;
    }
}

/* ==UserStyle==
@name           Github Catppuccin
@namespace      github.com/catppuccin/github
@version        1.1.14
@description    Soothing pastel theme for GitHub
@author         Catppuccin
@updateURL      https://github.com/catppuccin/github/raw/main/catppuccin.user.css
@preprocessor   stylus
@var select theme "Theme" ["Latte", "Frappe", "Macchiato", "Mocha*"]
@var select accent-color "Accent" ["Rosewater*", "Flamingo", "Pink", "Mauve", "Red", "Maroon", "Peach", "Yellow", "Green", "Teal", "Blue", "Sapphire", "Sky", "Lavender", "Gray"]
@var checkbox topbar "Topbar colors" 1
@var checkbox usefont "Use Inter font" 0
==/UserStyle== */

@-moz-document regexp("https:\/\/*github\.com") {

  if (theme=="Latte") {
    $type = light
