        /* chat list typing message */
        --typing: var(--green) !important;
        /* background for video player and image viewer */
        --media-viewer-background: var(--mantle) !important;
        /* not loaded media */
        --media-gallery-thumb-background: var(--crust) !important;

        /* Group info/Contact info */
        --photopicker-overlay-background: var(--mantle-backdrop) !important;
        --photopicker-overlay-background-rgb: var(--base-rgb) !important;
        --media-viewer-background-rgb: var(--base-rgb) !important;
        --drawer-background-deep: var(--crust) !important;
        /* group info read more */
        --input-button-more: var(--sapphire) !important;
        /* Group info thumbnails border */
        --chat-info-drawer-thumb-background: var(--surface2) !important;
        /* Group admin badge background */
        --chat-marker-background: var(--mantle) !important;
        --chat-marker-border: var(--mantle) !important;
        /* Group admin badge foreground */
        --chat-marker: var(--subtext0) !important;
        /* Media gallery */
        --drawer-gallery-background: var(--crust) !important;
        /* mute toggle */
        --switch-button-checked-color: var(--accent) !important;
        --switch-track-checked-color: var(--surface2) !important;
        --switch-track-color: var(--surface2) !important;
        --switch-button-color: var(--overlay1) !important;

        /* danger, block, exit and report buttons */
        --danger: var(--red) !important;

        /* group added by someone not in contacts */
        --button-plain-background: var(--surface0) !important;
        --button-plain-background-hover: var(--surface1) !important;

        /* EMOJI AND STICKERS */
        --panel-input-background: var(--surface0) !important;
        --sticker-button-background: var(--surface1) !important;
        --active-tab-marker: var(--accent) !important;

        /* REACTIONS */
        --reactions-panel-background-color: var(--surface0) !important;
        --reactions-tray-background: var(--surface0) !important;
        --reactions-details-background: var(--surface0) !important;
        --svg-gray-button: var(--surface0) !important;

        /* Forward message popup */
        --panel-background-colored-deeper: var(--crust) !important;
        --modal-backdrop: var(--mantle-backdrop) !important;

        /* MEDIA EDITOR */
        /* background for media editor */
        --panel-background-deeper: var(--mantle) !important;
        /* message box for media editor */
        --media-editor-image-caption-input-background: var(--surface0) !important;
        /* selected picture in media editor */
        --media-editor-thumb-border-active: var(--accent) !important;
        /* send button in media editor */
        --button-round-background: var(--accent) !important;

        /* COMPOSE BAR */
        /* type a message bar and background */
        --compose-input-background: var(--surface0) !important;
        --compose-input-border: var(--surface1) !important;
        --compose-panel-background: var(--mantle) !important;
        --rich-text-panel-background: var(--mantle) !important;
        /* select messages fixes */
        --panel-background: var(--mantle) !important;
        --panel-background-rgb: var(--mantle-rgb) !important;
        --checkbox-background: var(--accent) !important;
        /* quoted message in compose */
        --popup-panel-background: var(--surface0) !important; /* use Surface 0 for contrast, other colors don't pass AAA */
        /* mention list item background */
        --compose-panel-background-hover: var(--surface1) !important;
        /* compose bar icons */
        --icon: var(--accent) !important;
        /* ATTACH ICONS */
        /* poll icon */
        div._1OT67 > div > span > div > div > ul > li:nth-child(6) > button > span > svg > circle {
            fill: var(--green) !important;
        }
        /* image icon */
        div._1OT67 > div > span > div > div > ul > li:nth-child(1) > button > span > svg > g:nth-child(1) > g > path:nth-child(2) {
            fill: var(--pink) !important;
        }
        div._1OT67 > div > span > div > div > ul > li:nth-child(1) > button > span > svg > g:nth-child(1) > g > path:nth-child(1) {
            fill: var(--pink) !important;
            filter: brightness(85%) !important;
        }
        div._1OT67 > div > span > div > div > ul > li:nth-child(1) > button > span > svg > g:nth-child(1) > g > rect {
            fill: var(--pink) !important;
            filter: brightness(70%) !important;
        }
        /* sticker icon */
        div._1OT67 > div > span > div > div > ul > li:nth-child(2) > button > span > svg > g > circle {
            fill: var(--blue) !important;
            filter: brightness(85%) !important;
        }
        div._1OT67 > div > span > div > div > ul > li:nth-child(2) > button > span > svg > g > path:nth-child(2) {
            fill: var(--blue) !important;
        }
        /* camera icon */
        div._1OT67 > div > span > div > div > ul > li:nth-child(3) > button > span > svg > g:nth-child(1) > g > path:nth-child(1) {
            fill: var(--red) !important;
            filter: brightness(85%) !important;
        }
        div._1OT67 > div > span > div > div > ul > li:nth-child(3) > button > span > svg > g:nth-child(1) > g > path:nth-child(2) {
            fill: var(--red) !important;
        }
        div._1OT67 > div > span > div > div > ul > li:nth-child(3) > button > span > svg > g:nth-child(1) > g > rect {
            fill: var(--red) !important;
            filter: brightness(85%) !important;
        }
        /* document icon */
        div._1OT67 > div > span > div > div > ul > li:nth-child(4) > button > span > svg > g:nth-child(1) > g > path:nth-child(1) {
            fill: var(--mauve) !important;
            filter: brightness(85%) !important;
        }
        div._1OT67 > div > span > div > div > ul > li:nth-child(4) > button > span > svg > g:nth-child(1) > g > path:nth-child(2) {
            fill: var(--mauve) !important;
        }
        /* contact icon */
        div._1OT67 > div > span > div > div > ul > li:nth-child(5) > button > span > svg > g:nth-child(1) > g > g > path:nth-child(1) {
            fill: var(--sky) !important;
            filter: brightness(85%) !important;
        }
        div._1OT67 > div > span > div > div > ul > li:nth-child(5) > button > span > svg > g:nth-child(1) > g > g > path:nth-child(2) {
            fill: var(--sky) !important;
        }
        /* VOICE MESSAGE SENDING */
        --ptt-draft-button-send: var(--accent) !important;
        --ptt-draft-waveform-background: var(--base) !important;
        --ptt-draft-button-stop: var(--red) !important;
        --ptt-draft-button-stop-hover: var(--maroon) !important;

        /* GENERAL OPTIONS */

        /* dropdown menus */
        --dropdown-background: var(--surface0) !important;
        --dropdown-background-hover: var(--surface1) !important;

        /* OTHER SECTIONS */

        /* profile background */
        --drawer-background: var(--base) !important;
        /* communities, new chat, other panes */
        --panel-background-colored: var(--mantle) !important;
        --drawer-section-background: var(--base) !important;
        /* new chat icons */
        --round-icon-background: var(--green) !important;
        --inverse: var(--text) !important;
        /* title and icon for drawers */
        --drawer-header-title: var(--text) !important;
        /* buttons such as create community */
        --button-primary-background: var(--accent) !important;
        --button-primary-background-hover: var(--accent-darker) !important;
        /* keyboard shortcuts background */
        --panel-background-lighter: var(--mantle) !important;
        /* active input in modals and drawers */
        --input-border-active: var(--accent) !important;
    }

    /* Start page drawing */
    #app > div > div > div._2Ts6i._2xAQV > div > div > div.WM0_u > span > svg > path:nth-child(1) {
        fill: var(--surface2) !important;
    }

    /* Status unread dot */
    #df9d3429-f0ef-48b5-b5eb-f9d27b2deba6 > path:nth-child(2) {
        fill: var(--accent) !important;
    }

    #side > div._3gYev > div > div > button > div._3xdht._1ZD3q > span > svg > path {
        fill: var(--accent) !important;
    }
}

/* ==UserStyle==
@name           Youtube Music Catppuccin
@namespace      github.com/catppuccin/youtubemusic
@version        0.3.0
@description    Soothing pastel theme for Youtube Music
@author         Catppuccin
@preprocessor   stylus
@var select theme "Theme" ["Latte", "Frappe", "Macchiato", "Mocha*"]
@var select accent-color "Accent" ["Rosewater*", "Flamingo", "Pink", "Mauve", "Red", "Maroon", "Peach", "Yellow", "Green", "Teal", "Blue", "Sapphire", "Sky", "Lavender", "Text"]
==/UserStyle== */

@-moz-document domain("music.youtube.com") {



    if (theme == "Latte") {
        @import url("https://catppuccin.github.io/youtubemusic/src/latte.css");
    } else if (theme == "Frappe") {
        @import url("https://catppuccin.github.io/youtubemusic/src/frappe.css");
    } else if (theme == "Macchiato") {
        @import url("https://catppuccin.github.io/youtubemusic/src/macchiato.css");
    } else if (theme == "Mocha") {
        @import url("https://catppuccin.github.io/youtubemusic/src/mocha.css");
    }

    html:not(.style-scope) {    
        if (accent-color=="Rosewater") {
          --ctp-accent: var(--ctp-rosewater) !important;
      } else if (accent-color=="Flamingo") {
          --ctp-accent: var(--ctp-flamingo) !important;
      } else if (accent-color=="Pink") {
          --ctp-accent: var(--ctp-pink) !important;
      } else if (accent-color=="Mauve") {
          --ctp-accent: var(--ctp-mauve) !important;
      } else if (accent-color=="Red") {
          --ctp-accent: var(--ctp-red) !important;
      } else if (accent-color=="Maroon") {
          --ctp-accent: var(--ctp-maroon) !important;
      } else if (accent-color=="Peach") {
          --ctp-accent: var(--ctp-peach) !important;
      } else if (accent-color=="Yellow") {
          --ctp-accent: var(--ctp-yellow) !important;
      } else if (accent-color=="Green") {
          --ctp-accent: var(--ctp-green) !important;
      } else if (accent-color=="Teal") {
          --ctp-accent: var(--ctp-teal) !important;
      } else if (accent-color=="Blue") {
          --ctp-accent: var(--ctp-blue) !important;
      } else if (accent-color=="Sapphire") {
          --ctp-accent: var(--ctp-sapphire) !important;
      } else if (accent-color=="Sky") {
          --ctp-accent: var(--ctp-sky) !important;
      } else if (accent-color=="Lavender") {
